<script setup>
import {ref} from 'vue'
import request from "@/utils/request";
import {ElMessage} from 'element-plus';

const input1 = ref('瓜')
const input2 = ref('超')
const input3 = ref('頔')
const input4 = ref('洛')
const input5 = ref('乐')
const input6 = ref('馨')
const input7 = ref('刀')
const input8 = ref('绵')

const load = () => {
  request.get('/animal')
}
load()
const swapRandomInputs=()=> {
  request.get('/AnimalRandom').then(res => {
    console.log("请求成功")
  }).catch(err => {
    console.log("请求失败")
  })
  ElMessage.success("随机成功")
  for (let n = 0; n < 5; n++) {
    // 创建一个数组来存储所有非空输入框的引用
    const nonEmptyInputs = [input1, input2, input3, input4, input5, input6, input7, input8].filter(input => input.value !== '');

    // 如果没有足够的非空输入框，则不进行操作
    if (nonEmptyInputs.length < 2) return;

    // 使用 Fisher-Yates 洗牌算法随机排列非空输入框的值
    for (let i = nonEmptyInputs.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [nonEmptyInputs[i].value, nonEmptyInputs[j].value] = [nonEmptyInputs[j].value, nonEmptyInputs[i].value];
    }
  }

}

const clear = () => {
  input1.value = ''
  input2.value = ''
  input3.value = ''
  input4.value = ''
  input5.value = ''
  input6.value = ''
  input7.value = ''
  input8.value = ''
  ElMessage.success("清空成功")
}
const reset = () => {
  input1.value = '瓜'
  input2.value = '超'
  input3.value = '頔'
  input4.value = '洛'
  input5.value = '乐'
  input6.value = '馨'
  input7.value = '刀'
  input8.value = '绵'
  ElMessage.success("重置成功")
}
</script>

